class AuntieRuthApp{constructor(){this.components=new Map,this.dataManager=null,this.initialized=!1}async init(){if(!this.initialized){console.log("Initializing AuntieRuth.com modernization...");try{this.dataManager=new DataManager,await this.initNavigation();"person"===this.detectPageType()&&await this.initPersonPageEnhancements(),this.isMobile()&&await this.initMobileEnhancements(),this.initialized=!0,console.log("Application initialized successfully")}catch(error){console.error("Failed to initialize application:",error)}}}async initNavigation(){try{const NavigationModule=await this.loadComponent("navigation-enhanced");if(NavigationModule){const nav=new NavigationModule.default({dataManager:this.dataManager});await nav.init(),this.components.set("navigation",nav)}}catch(error){console.error("Failed to initialize navigation:",error),this.initFallbackNavigation()}}async initPersonPageEnhancements(){console.log("Enhancing person page experience...")}async initMobileEnhancements(){console.log("Applying mobile optimizations..."),document.body.classList.add("mobile-device")}async loadComponent(name){try{return await import(`../components/${name}.js`)}catch(error){return console.warn(`Component ${name} not found, using fallback`),null}}initFallbackNavigation(){console.log("Using fallback navigation")}detectPageType(){const filename=window.location.pathname.split("/").pop()||"";return filename.startsWith("XF")?"person":filename.startsWith("THF")?"thumbnail":filename.startsWith("XI")?"image":filename.includes("index")?"index":"unknown"}isMobile(){return window.innerWidth<=768||"ontouchstart"in window||navigator.maxTouchPoints>0}}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",()=>{window.auntieRuthApp=new AuntieRuthApp,window.auntieRuthApp.init()}):(window.auntieRuthApp=new AuntieRuthApp,window.auntieRuthApp.init());